import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table } from "react-bootstrap";

function ProductList() {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchProducts() {
            try {
                const response = await axios.get("http://localhost:5000/api/products");
                setProducts(response.data);
                setLoading(false);
            } catch (error) {
                console.error("Error fetching products:", error);
                setLoading(false);
            }
        }
        fetchProducts();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    const renderImage = (imgProducto) => {
        if (!imgProducto) return null;

        const url = URL.createObjectURL(new Blob([imgProducto]));
        return <img src={url} alt="Product" style={{ width: "100px", height: "100px" }} />;
    };

    return (
        <div>
            <h1>Product List</h1>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Precio Antiguo</th>
                        <th>Precio Promocional</th>
                        <th>Imagen</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.ID}>
                            <td>{product.ID}</td>
                            <td>{product.PrecioAnt}</td>
                            <td>{product.PrecioPromo}</td>
                            <td>{renderImage(product.imgProducto)}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
}

export default ProductList;
