import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Form, Table, Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const ProductCRUD = () => {
    const [products, setProducts] = useState([]);
    const [show, setShow] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [form, setForm] = useState({ PrecioAnt: '', PrecioPromo: '', imgProducto: '' });

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/products'); // URL del servidor
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleFileChange = (e) => {
        setForm({ ...form, imgProducto: e.target.files[0] });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            Object.keys(form).forEach((key) => {
                formData.append(key, form[key]);
            });

            if (selectedProduct) {
                await axios.put(`http://localhost:5000/api/products/${selectedProduct.ID}`, formData, {
                    headers: { 'Content-Type': 'multipart/form-data' }
                });
            } else {
                await axios.post('http://localhost:5000/api/products', formData, {
                    headers: { 'Content-Type': 'multipart/form-data' }
                });
            }
            fetchProducts();
            handleClose();
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    const handleEdit = (product) => {
        setSelectedProduct(product);
        setForm({ PrecioAnt: product.PrecioAnt, PrecioPromo: product.PrecioPromo, imgProducto: '' });
        setShow(true);
    };

    const handleDelete = async (ID) => {
        try {
            await axios.delete(`http://localhost:5000/api/products/${ID}`);
            fetchProducts();
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    const handleClose = () => {
        setShow(false);
        setSelectedProduct(null);
        setForm({ PrecioAnt: '', PrecioPromo: '', imgProducto: '' });
    };

    const renderImage = (imgFileName) => {
        return imgFileName ? `http://localhost:5000/uploads/${imgFileName}` : null;
    };

    return (
        <div>
            <Button variant="primary" onClick={() => setShow(true)}>Add Product</Button>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Precio Antiguo</th>
                        <th>Precio Promoción</th>
                        <th>Imagen</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.ID}>
                            <td>{product.ID}</td>
                            <td>{product.PrecioAnt}</td>
                            <td>{product.PrecioPromo}</td>
                            <td>
                                <img src={renderImage(product.imgProducto)} alt="Product" width="50" />
                            </td>
                            <td>
                                <Button variant="warning" onClick={() => handleEdit(product)}>Edit</Button>
                                <Button variant="danger" onClick={() => handleDelete(product.ID)}>Delete</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{selectedProduct ? 'Edit Product' : 'Add Product'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="formPrecioAnt">
                            <Form.Label>Precio Antiguo</Form.Label>
                            <Form.Control
                                type="number"
                                name="PrecioAnt"
                                value={form.PrecioAnt}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>
                        <Form.Group controlId="formPrecioPromo">
                            <Form.Label>Precio Promoción</Form.Label>
                            <Form.Control
                                type="number"
                                name="PrecioPromo"
                                value={form.PrecioPromo}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>
                        <Form.Group controlId="formImgProducto">
                            <Form.Label>Imagen del Producto</Form.Label>
                            <Form.Control
                                type="file"
                                name="imgProducto"
                                onChange={handleFileChange}
                                accept="image/*"
                            />
                        </Form.Group>
                        <Button variant="primary" type="submit">
                            {selectedProduct ? 'Update Product' : 'Add Product'}
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </div>
    );
};

export default ProductCRUD;
